content:
{
  "$schema": "https://schemas.wp.org/trunk/block.json",
  "apiVersion": 2,
  "name": "julianboelen/section-process",
  "version": "1.0.0",
  "title": "Section Process",
  "category": "julianboelen-blocks",
  "icon": "list-view",
  "description": "A streamlined process section with customizable step cards",
  "keywords": ["process", "steps", "cards", "workflow", "timeline"],
  "supports": {
    "html": false,
    "anchor": true,
    "customClassName": true,
    "inserter": true,
    "multiple": true,
    "reusable": true,
    "spacing": {
      "padding": true,
      "margin": true
    },
    "typography": {
      "fontSize": true,
      "lineHeight": true
    },
    "color": {
      "background": true,
      "text": true
    }
  },
  "attributes": {
    "sectionTitle": {
      "type": "string",
      "default": "Een gestroomlijnd proces"
    },
    "backgroundColor": {
      "type": "string",
      "default": "#f9fafb"
    },
    "titleColor": {
      "type": "string",
      "default": "#111827"
    },
    "cardBackgroundColor": {
      "type": "string",
      "default": "#ffffff"
    },
    "cardTextColor": {
      "type": "string",
      "default": "#374151"
    },
    "cardTitleColor": {
      "type": "string",
      "default": "#111827"
    },
    "enableHoverEffect": {
      "type": "boolean",
      "default": true
    },
    "columnsDesktop": {
      "type": "number",
      "default": 4
    },
    "columnsTablet": {
      "type": "number",
      "default": 2
    },
    "columnsMobile": {
      "type": "number",
      "default": 1
    },
    "cardGap": {
      "type": "string",
      "default": "6"
    },
    "processSteps": {
      "type": "array",
      "default": [
        {
          "id": "step-1",
          "stepNumber": "01",
          "title": "Solliciteren",
          "description": "Solliciteer door jouw CV of Linkedin profiel op te sturen of direct op een vacature te reageren.",
          "imageUrl": "https://images.unsplash.com/photo-1595147389795-37094173bfd8?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w4MTAzMDV8MHwxfHNlYXJjaHwzfHxpbWFnZXxlbnwwfDB8fHwxNzU5NjYzODYwfDA&ixlib=rb-4.1.0&q=80&w=1080&w=800&h=600&fit=crop",
          "imageAlt": "Solliciteren",
          "imageId": null
        },
        {
          "id": "step-2",
          "stepNumber": "02",
          "title": "Profiel opstellen",
          "description": "Solliciteer door jouw CV of Linkedin profiel op te sturen of direct op een vacature te reageren.",
          "imageUrl": "https://images.unsplash.com/photo-1595147389795-37094173bfd8?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w4MTAzMDV8MHwxfHNlYXJjaHwzfHxpbWFnZXxlbnwwfDB8fHwxNzU5NjYzODYwfDA&ixlib=rb-4.1.0&q=80&w=1080&w=800&h=600&fit=crop",
          "imageAlt": "Profiel opstellen",
          "imageId": null
        },
        {
          "id": "step-3",
          "stepNumber": "03",
          "title": "Op gesprek",
          "description": "Solliciteer door jouw CV of Linkedin profiel op te sturen of direct op een vacature te reageren.",
          "imageUrl": "https://images.unsplash.com/photo-1488372759477-a7f4aa078cb6?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w4MTAzMDV8MHwxfHNlYXJjaHw1fHxpbWFnZXxlbnwwfDB8fHwxNzU5NjYzODYwfDA&ixlib=rb-4.1.0&q=80&w=1080&w=800&h=600&fit=crop",
          "imageAlt": "Op gesprek",
          "imageId": null
        },
        {
          "id": "step-4",
          "stepNumber": "04",
          "title": "Aan de slag!",
          "description": "Solliciteer door jouw CV of Linkedin profiel op te sturen of direct op een vacature te reageren.",
          "imageUrl": "https://images.unsplash.com/photo-1595147389795-37094173bfd8?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w4MTAzMDV8MHwxfHNlYXJjaHwzfHxpbWFnZXxlbnwwfDB8fHwxNzU5NjYzODYwfDA&ixlib=rb-4.1.0&q=80&w=1080&w=800&h=600&fit=crop",
          "imageAlt": "Aan de slag",
          "imageId": null
        }
      ]
    },
    "paddingTop": {
      "type": "string",
      "default": "12"
    },
    "paddingBottom": {
      "type": "string",
      "default": "12"
    },
    "titleMarginBottom": {
      "type": "string",
      "default": "8"
    },
    "cardBorderRadius": {
      "type": "string",
      "default": "lg"
    },
    "imageBorderRadius": {
      "type": "string",
      "default": "none"
    },
    "shadowStyle": {
      "type": "string",
      "default": "md"
    },
    "hoverShadowStyle": {
      "type": "string",
      "default": "lg"
    }
  },
  "textdomain": "julianboelen",
  "editorScript": "julianboelen-section-process-editor",
  "style": "file:./style.css",
  "render": "file:./render.php"
}
