{
  "$schema": "https://schemas.wp.org/trunk/block.json",
  "apiVersion": 2,
  "name": "julianboelen/section-text-image",
  "version": "1.0.0",
  "title": "Section Text Image",
  "category": "julianboelen-blocks",
  "icon": "align-pull-left",
  "description": "A sophisticated two-column section with text content and image, featuring responsive design and advanced customization options",
  "keywords": ["section", "text", "image", "two-column", "content"],
  "supports": {
    "html": false,
    "anchor": true,
    "customClassName": true,
    "inserter": true,
    "multiple": true,
    "reusable": true,
    "spacing": {
      "padding": true,
      "margin": true
    },
    "typography": {
      "fontSize": true,
      "lineHeight": true
    }
  },
  "attributes": {
    "smallHeading": {
      "type": "string",
      "default": "Dit is Starapple:"
    },
    "mainHeading": {
      "type": "string",
      "default": "De specialist achter de match"
    },
    "description": {
      "type": "string",
      "default": "StarApple is geen standaard bemiddelaar. Bij ons draait het om de perfecte interim match. Wij combineren inhoudelijke IT-expertise met een persoonlijke, gedreven aanpak. Of je nu zoekt naar de juiste interim opdracht of tijdelijke versterking, bij ons weet je precies wie je aan de lijn hebt en waar je aan toe bent. Leer ons kennen."
    },
    "buttonText": {
      "type": "string",
      "default": "Over ons"
    },
    "buttonUrl": {
      "type": "string",
      "default": "#"
    },
    "buttonTarget": {
      "type": "string",
      "default": ""
    },
    "buttonRel": {
      "type": "string",
      "default": ""
    },
    "showButton": {
      "type": "boolean",
      "default": true
    },
    "buttonBackgroundType": {
      "type": "string",
      "default": "primary",
      "enum": ["primary", "secondary", "custom"]
    },
    "customButtonColor": {
      "type": "string",
      "default": "#9333ea"
    },
    "imageUrl": {
      "type": "string",
      "default": "https://images.unsplash.com/photo-1621155346337-1d19476ba7d6?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w4MTAzMDV8MHwxfHNlYXJjaHw0fHxpbWFnZXxlbnwwfDB8fHwxNzU5NjYzODYwfDA&ixlib=rb-4.1.0&q=80&w=1080&w=1200&h=800&fit=crop"
    },
    "imageAlt": {
      "type": "string",
      "default": "Team collaboration"
    },
    "imageId": {
      "type": "number",
      "default": 0
    },
    "imagePosition": {
      "type": "string",
      "default": "right",
      "enum": ["left", "right"]
    },
    "backgroundColor": {
      "type": "string",
      "default": "#f9fafb"
    },
    "textColor": {
      "type": "string",
      "default": "#1f2937"
    },
    "smallHeadingColor": {
      "type": "string",
      "default": "#4b5563"
    },
    "descriptionColor": {
      "type": "string",
      "default": "#374151"
    },
    "contentAlignment": {
      "type": "string",
      "default": "left",
      "enum": ["left", "center"]
    },
    "imageRoundness": {
      "type": "string",
      "default": "3xl",
      "enum": ["none", "lg", "xl", "2xl", "3xl", "full"]
    },
    "columnGap": {
      "type": "string",
      "default": "12",
      "enum": ["8", "10", "12", "16"]
    },
    "verticalPadding": {
      "type": "string",
      "default": "12",
      "enum": ["8", "12", "16", "20"]
    },
    "showShadow": {
      "type": "boolean",
      "default": true
    }
  },
  "textdomain": "julianboelen",
  "editorScript": "julianboelen-section-text-image-editor",
  "style": "file:./style.css",
  "render": "file:./render.php"
}
