{
  "$schema": "https://schemas.wp.org/trunk/block.json",
  "apiVersion": 2,
  "name": "julianboelen/section-title-text-horizontal",
  "version": "1.0.0",
  "title": "Section Title Text Horizontal",
  "category": "julianboelen-blocks",
  "icon": "columns",
  "description": "A sophisticated two-column layout with heading on the left and body text on the right",
  "keywords": ["section", "title", "text", "horizontal", "two-column", "layout"],
  "supports": {
    "html": false,
    "anchor": true,
    "customClassName": true,
    "inserter": true,
    "multiple": true,
    "reusable": true,
    "spacing": {
      "padding": true,
      "margin": true
    },
    "typography": {
      "fontSize": true,
      "lineHeight": true
    },
    "color": {
      "background": true,
      "text": true
    }
  },
  "attributes": {
    "heading": {
      "type": "string",
      "default": "Wij zijn Starapple en wij willen talent laten groeien!"
    },
    "paragraph1": {
      "type": "string",
      "default": "Een brug slaan tussen aanbod van en vraag naar uiterst specifieke IT-specialisten, dat is Starapple in één zin."
    },
    "paragraph2": {
      "type": "string",
      "default": "Wij helpen je graag in jou zoektocht naar een nieuwe uitdaging zodat jij jouw carrière verder kunt ontwikkelen."
    },
    "backgroundColor": {
      "type": "string",
      "default": "#ffffff"
    },
    "headingColor": {
      "type": "string",
      "default": "#111827"
    },
    "textColor": {
      "type": "string",
      "default": "#111827"
    },
    "headingSize": {
      "type": "string",
      "default": "large",
      "enum": ["medium", "large", "xlarge"]
    },
    "textSize": {
      "type": "string",
      "default": "base",
      "enum": ["small", "base", "large"]
    },
    "columnRatio": {
      "type": "string",
      "default": "5-7",
      "enum": ["4-8", "5-7", "6-6"]
    },
    "verticalAlignment": {
      "type": "string",
      "default": "start",
      "enum": ["start", "center", "end"]
    },
    "columnGap": {
      "type": "string",
      "default": "large",
      "enum": ["small", "medium", "large", "xlarge"]
    },
    "paddingTop": {
      "type": "string",
      "default": "16"
    },
    "paddingBottom": {
      "type": "string",
      "default": "16"
    },
    "maxWidth": {
      "type": "string",
      "default": "7xl",
      "enum": ["5xl", "6xl", "7xl", "full"]
    },
    "showParagraph2": {
      "type": "boolean",
      "default": true
    },
    "paragraphSpacing": {
      "type": "string",
      "default": "6"
    }
  },
  "textdomain": "julianboelen",
  "editorScript": "julianboelen-section-title-text-horizontal-editor",
  "style": "file:./style.css",
  "render": "file:./render.php"
}
